mapper.select:
select *
from ChatUser
where crNo = #{crNo}
  and mno = #{mNo}
  and status = 'n'

mapper.update:
update ChatUser
set chNo= #{chNo}
where userNo = #{userNo}
  and status = 'n'

mapper.select:
select userNo
from ChatUser
where mNo = #{mNo}
  and crNo = #{crNo}
  and status = 'n'

select m.nick
from member m,
     chatUser cu
where cu.userNo = #{userNo}
  and cu.mNo = m.mNo

select cu.*
from ChatUser cu,
     chatRoom cr
where cu.mNo = #{mNo}
  and cu.status = 'n'
  and cr.crNo = cu.crNo
order by cr.updateTime desc;

select count(*)
from chatUser
where mNo = #{mNo}
  and crNo = #{crNo}
  and status = 'n'

mapper.update:
update ChatUser
set chNo = #{chNo}
where mNo = #{mNo}
  and crNo = #{crNo}

mapper.select:
select chNo
from ChatUser
where userNo = #{userNo}
